import React from 'react';
import { useTable, usePagination } from "react-table";
import MaUTable from "@material-ui/core/Table";
import TableBody from "@material-ui/core/TableBody";
import TableCell from "@material-ui/core/TableCell";
import TableHead from "@material-ui/core/TableHead";
import TableRow from "@material-ui/core/TableRow";


const data = [
    { district: "Wan Chai", building: "Block C, Villa Lotto", cases: "2", ages: ["21", "24"], resident: "HK Resident", date: "05/03/2020" },
    { district: "Tai Po", building: "Tower 1, La Grove", cases: "1", ages: ["45"], resident: "Non-local", date: "05/04/2020" },
    { district: "Central and Western", building: "18 Stanley Main Street", cases: "1", ages: ["33"], resident: "HK Resident", date: "11/03/2020" },
    { district: "Wan Chai", building: "Block C, Villa Lotto", cases: "2", ages: ["21", "24"], resident: "HK Resident", date: "05/03/2020" },
    { district: "Tai Po", building: "Tower 1, La Grove", cases: "1", ages: ["45"], resident: "Non-local", date: "05/04/2020" },
    { district: "Central and Western", building: "18 Stanley Main Street", cases: "1", ages: ["33"], resident: "HK Resident", date: "11/03/2020" },
    { district: "Wan Chai", building: "Block C, Villa Lotto", cases: "2", ages: ["21", "24"], resident: "HK Resident", date: "05/03/2020" },
    { district: "Tai Po", building: "Tower 1, La Grove", cases: "1", ages: ["45"], resident: "Non-local", date: "05/04/2020" },
    { district: "Central and Western", building: "18 Stanley Main Street", cases: "1", ages: ["33"], resident: "HK Resident", date: "11/03/2020" },
];


const columns = [
    {
        Header: "District",
        accessor: "district",
        Footer: "Name",
    },
    {
        Header: "Building",
        accessor: "building",
        Footer: "Name",
    },
    {
        Header: "Case No.",
        accessor: "cases"
    },
    {
        Header: "Ages",
        accessor: "ages"
    },
    {
        Header: "HK/Non-HK Resident",
        accessor: "resident"
    },
    {
        Header: "Date",
        accessor: "date"
    }
]


const TableData = () => {

    const {
        getTableProps,
        getTableBodyProps,
        headerGroups,
        prepareRow,
        page,
        canPreviousPage,
        canNextPage,
        pageOptions,
        pageCount,
        gotoPage,
        nextPage,
        previousPage,
        setPageSize,
        state: { pageIndex, pageSize }
    } = useTable(
        {
            columns,
            data,
            initialState: { pageIndex: 0 }
        },
        usePagination
    );


    return (
        <>
            <table {...getTableProps()}>
                <thead>
                    {headerGroups.map(headerGroup => (
                        <tr {...headerGroup.getHeaderGroupProps()}>
                            {headerGroup.headers.map(column => (
                                <th {...column.getHeaderProps()}>{column.render("Header")}</th>
                            ))}
                        </tr>
                    ))}
                </thead>
                <tbody {...getTableBodyProps()}>
                    {page.map((row, i) => {
                        prepareRow(row);
                        return (
                            <tr {...row.getRowProps()}>
                                {row.cells.map(cell => {
                                    return (
                                        <td {...cell.getCellProps()}>{cell.render("Cell")}</td>
                                    );
                                })}
                            </tr>
                        );
                    })}
                </tbody>
            </table>
            <div>
                <button onClick={() => gotoPage(0)} disabled={!canPreviousPage}>
                    {"<<"}
                </button>{" "}
                <button onClick={() => previousPage()} disabled={!canPreviousPage}>
                    {"<"}
                </button>{" "}
                <button onClick={() => nextPage()} disabled={!canNextPage}>
                    {">"}
                </button>{" "}
                <button onClick={() => gotoPage(pageCount - 1)} disabled={!canNextPage}>
                    {">>"}
                </button>{" "}
                <span>
                    Page{" "}
                    <strong>
                        {pageIndex + 1} of {pageOptions.length}
                    </strong>{" "}
                </span>
                <span>
                    | Go to page:{" "}
                    <input
                        type="number"
                        defaultValue={pageIndex + 1}
                        onChange={e => {
                            const page = e.target.value ? Number(e.target.value) - 1 : 0;
                            gotoPage(page);
                        }}
                        style={{ width: "100px" }}
                    />
                </span>{" "}
                <select
                    value={pageSize}
                    onChange={e => {
                        setPageSize(Number(e.target.value));
                    }}
                >
                    {[10, 20, 30, 40, 50].map(pageSize => (
                        <option key={pageSize} value={pageSize}>
                            Show {pageSize}
                        </option>
                    ))}
                </select>
            </div>
        </>
    )

}

export default TableData;
