import React from 'react';
import { useTable, useFilters, useGlobalFilter, usePagination } from "react-table";
import MaUTable from "@material-ui/core/Table";
import TableBody from "@material-ui/core/TableBody";
import TableCell from "@material-ui/core/TableCell";
import TableHead from "@material-ui/core/TableHead";
import TableRow from "@material-ui/core/TableRow";


const data = [
    { district: "Wan Chai", building: "Block C, Villa Lotto", cases: "2", ages: ["21", "24"], resident: "HK Resident", date: "05/03/2020" },
    { district: "Tai Po", building: "Tower 1, La Grove", cases: "1", ages: ["45"], resident: "Non-local", date: "05/04/2020" },
    { district: "Central and Western", building: "18 Stanley Main Street", cases: "1", ages: ["33"], resident: "HK Resident", date: "11/03/2020" },
    { district: "Wan Chai", building: "Block C, Villa Lotto", cases: "2", ages: ["21", "24"], resident: "HK Resident", date: "05/03/2020" },
    { district: "Tai Po", building: "Tower 1, La Grove", cases: "1", ages: ["45"], resident: "Non-local", date: "05/04/2020" },
    { district: "Central and Western", building: "18 Stanley Main Street", cases: "1", ages: ["33"], resident: "HK Resident", date: "11/03/2020" },
    { district: "Wan Chai", building: "Block C, Villa Lotto", cases: "2", ages: ["21", "24"], resident: "HK Resident", date: "05/03/2020" },
    { district: "Tai Po", building: "Tower 1, La Grove", cases: "1", ages: ["45"], resident: "Non-local", date: "05/04/2020" },
    { district: "Central and Western", building: "18 Stanley Main Street", cases: "1", ages: ["33"], resident: "HK Resident", date: "11/03/2020" },
];


const columns = [
    {
        Header: "District",
        accessor: "district",
        Footer: "Name",
    },
    {
        Header: "Building",
        accessor: "building",
        Footer: "Name",
    },
    {
        Header: "Case No.",
        accessor: "cases"
    },
    {
        Header: "Ages",
        accessor: "ages"
    },
    {
        Header: "HK/Non-HK Resident",
        accessor: "resident"
    },
    {
        Header: "Date",
        accessor: "date"
    }
]

const DefaultColumnFilter = ({
    column: { filterValue, preFilteredRows, setFilter }
}) => {
    const count = preFilteredRows.length;

    return (
        <input
            value={filterValue || ""}
            onChange={e => {
                setFilter(e.target.value || undefined);
            }}
            placeholder={`Search ${count} records...`}
        />
    );
};

const GlobalFilter = ({
    preGlobalFilteredRows,
    globalFilter,
    setGlobalFilter
}) => {
    const count = preGlobalFilteredRows && preGlobalFilteredRows.length;

    return (
        <span>
            Search:{" "}
            <input
                value={globalFilter || ""}
                onChange={e => {
                    setGlobalFilter(e.target.value || undefined); // Set undefined to remove the filter entirely
                }}
                placeholder={`${count} records...`}
                style={{
                    border: "0"
                }}
            />
        </span>
    );
};

const TableData = () => {
    const filterTypes = React.useMemo(
        () => ({
            text: (rows, id, filterValue) => {
                return rows.filter(row => {
                    const rowValue = row.values[id];
                    return rowValue !== undefined
                        ? String(rowValue)
                            .toLowerCase()
                            .startsWith(String(filterValue).toLowerCase())
                        : true;
                });
            }
        }),
        []
    );

    const defaultColumn = React.useMemo(
        () => ({
            Filter: DefaultColumnFilter
        }),
        []
    );

    const {
        getTableProps,
        getTableBodyProps,
        headerGroups,
        rows,
        prepareRow,
        state,
        visibleColumns,
        preGlobalFilteredRows,
        setGlobalFilter
    } = useTable(
        {
            columns,
            data,
            defaultColumn,
            filterTypes
        },
        useFilters,
        useGlobalFilter
    );

    return (
        <table {...getTableProps()}>
            <thead>
                {headerGroups.map(headerGroup => (
                    <tr {...headerGroup.getHeaderGroupProps()}>
                        {headerGroup.headers.map(column => (
                            <th {...column.getHeaderProps()}>
                                {column.render("Header")}
                                <div>{column.canFilter ? column.render("Filter") : null}</div>
                            </th>
                        ))}
                    </tr>
                ))}
                <tr>
                    <th
                        colSpan={visibleColumns.length}
                        style={{
                            textAlign: "left"
                        }}
                    >
                        <GlobalFilter
                            preGlobalFilteredRows={preGlobalFilteredRows}
                            globalFilter={state.globalFilter}
                            setGlobalFilter={setGlobalFilter}
                        />
                    </th>
                </tr>
            </thead>
            <tbody {...getTableBodyProps()}>
                {rows.map((row, i) => {
                    prepareRow(row);
                    return (
                        <tr {...row.getRowProps()}>
                            {row.cells.map(cell => {
                                return <td {...cell.getCellProps()}>{cell.render("Cell")}</td>;
                            })}
                        </tr>
                    );
                })}
            </tbody>
        </table>
    );
};


<MaUTable {...getTableProps()}>
    <TableHead>
        {headerGroups.map(headerGroup => (
            <TableRow {...headerGroup.getHeaderGroupProps()}>
                {headerGroup.headers.map(column => (
                    <TableCell {...column.getHeaderProps()}>
                        {column.render("Header")}
                    </TableCell>
                ))}
            </TableRow>
        ))}
    </TableHead>
    <TableBody>
        {rows.map((row, i) => {
            prepareRow(row);
            return (
                <TableRow {...row.getRowProps()}>
                    {row.cells.map(cell => {
                        return (
                            <TableCell {...cell.getCellProps()}>
                                {cell.render("Cell")}
                            </TableCell>
                        );
                    })}
                </TableRow>
            );
        })}
    </TableBody>
</MaUTable>


export default TableData;
